INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_5', 'El placer de volver a verte, amigo. ¿En qué puedo ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_6', 'Usted es siempre bienvenido aquí, amigo. ¿Qué hay en tu mente?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_7', 'Hola, mi amigo. Espero que hayan llegado a beneficiar tanto a nuestras grandes naciones?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_8', 'Es muy bueno ver una cara amiga. ¿En qué puedo ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_9', 'Bienvenido, amigo. Por favor, quédate. Tendré mis siervos preparar una fiesta maravillosa para celebrar su llegada.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_10', 'Hoy nos reunimos, mi amigo. ¿Cómo puede mi gloriosa nación ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_11', 'Tenía la sensación de su cara amable aparecería por aquí. ¿Qué puede hacer mi imperio para ayudar a un líder honorable como usted?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_12', 'Mis ciudadanos han quedado satisfechos con contacto de su imperio. Esto sólo puede beneficiar a ambos. Ahora bien, ¿en qué puedo ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_13', 'Genial para ver un amigo. ¿En qué podemos ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_14', 'Usted ha venido al lugar correcto para la diplomacia. ¿Qué quieres que discutir?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_7', 'Hola, ¿qué negocio tiene usted conmigo hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_8', '¿En qué puedo ayudarle? ¿Y cómo puede usted ayudarme?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_9', 'Hola. Espero que vengas delante de mí con rectitud de intención.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_10', 'Hola. ¿Puedo presentar algo de mi mejor vino mientras conversamos?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_11', '¿En qué puedo ayudarle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_12', 'Saludos. ¿Qué cosas qué quieres hablar hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_13', 'Hola. ¿En qué puedo ayudarle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_6', 'He asuntos urgentes que atender. No me hagas perder el tiempo aquí.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_7', 'Su empresa no es bienvenido aquí. Te ruego que has hecho tu visita a mi tierra corta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_8', 'Usted está perdiendo su tiempo aquí. ¿Qué podría posiblemente tenga que ofrecerme?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_9', '¿No envíe su emisario de distancia? Tienes un montón de nervios que viene a visitarme.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_10', 'No me pongas a prueba. Usted no está en condiciones de pagar más hostilidad con nosotros. Ahora, ¿qué es lo que quieres?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_11', 'En declaraciones a que no estaba en mi lista de prioridades en la actualidad. ¿Qué es lo que quieres con mi tiempo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_12', 'Oh, excelente. Es usted. Dime, ¿por qué me has deshonrado con su hedor grosero?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_13', 'Usted no merecen mi tiempo. Ahora, vete. Así que, ¿por qué sigues aquí?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_14', 'Ya es bastante malo tener que lidiar con su inmundicia que viven en su propia cala. Ahora que te has arrastrado hasta mi presencia?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_15', 'Quisiera mostrar mi respeto y le ofrecen vino, pero usted es un poco bruta indigno que sólo merece ser alimentados con veneno. Ahora, manos a la obra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_4', 'Debemos poner fin a esta lucha. Tanto nuestro pueblo clama a poner fin a esta violencia sin sentido.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_5', 'Estoy dispuesto a negociar un alto el fuego, si lo eres. Esta guerra ha durado demasiado tiempo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_6', 'La falta de terminar esta guerra ahora sólo te hace parecer más al villano. Por favor, poner fin a esta locura y vamos a volver a la paz.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_7', '¿No hay una manera más civil para poner fin a nuestras hostilidades?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_8', 'La lucha contra este mundo sólo hace peor. Nosotros no somos bárbaros. Tenemos que dejar de actuar como tal.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_9', 'Buen líder, ¿me ayudará a poner fin a esta tensión? Es sólo mi esperanza de construir la paz entre nuestros pueblos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_10', 'Les pedimos nada más que un retorno a la sensibilidad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_11', 'Es hora de dejar el pasado detrás de nosotros y resolver nuestras diferencias. El derramamiento de sangre no es la respuesta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_12', 'Es hora de que nos damos cuenta de que lo único que hemos logrado aquí se intercambia dolor por nuestros seres queridos perdidos. Debemos poner fin a esta guerra, por el bien de nuestro pueblo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_5', '¿De verdad creías que ibas a salir de ésta con tanta facilidad? Se ha ido!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_6', '¿Por qué estás aquí? Esto es una broma, ¿verdad?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_9', 'Es hora de enfrentar la realidad. Yo gano, tú pierdes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_10', 'No habrá paz. Todas sus ciudades se nos pertenecen.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_11', 'Es hora de enfrentar la realidad. Yo gano, tú pierdes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_12', 'Su tierra está llena de delincuentes, y yo soy su juez. Ahora, fuera de mi vista, tengo un asunto que resolver.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_13', '¿Oyes eso? Ese es el sonido de su pecado clamando por la salvación. Lo bueno es que estoy aquí.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_2', 'Espero que sus científicos están trabajando tan duro como podamos cumplir nuestro acuerdo de investigación. Ruego se hace vale la pena el dinero que pasamos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_3', 'Dios te salve, líder poderoso. ¿Eres científicos duros en el trabajo que completan nuestro acuerdo de investigación?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_4', 'Pasamos un montón de oro en ese acuerdo de investigación. Espero que un descubrimiento es de alrededor de la esquina?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_2', 'Es bueno verte de nuevo, amigo. Espero que su imperio está en condición estable. ¿En qué puedo ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_3', 'Salve, amigo! ¿Cómo puedo ayudar a su gran nación hoy en día?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_4', 'Es bueno verte de nuevo, amigo. ¿Necesitas algo de mí?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_5', 'Saludos, amigo. Tenemos negocios para llevar a cabo, ¿no?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_4', 'Estoy harto de {1_ThirdPlayerName: textkey}. Espero que algún día él será expulsado de nuestras vidas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_5', 'No entiendo cómo villanos como {1_ThirdPlayerName:textkey}  puede llamar a su gente civilizada. Tienen que ser estúpido.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_6', 'No está de acuerdo conmigo en que {1_ThirdPlayerName: textkey} es un problema para el mundo entero? Tenemos que decir a los demás acerca de su pecado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_2', 'Usted no ha sido destruido todavía? Estoy sorprendido. Yo creía que los bárbaros eran más fáciles de vencer que eso. Uno sólo puede esperar.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_3', 'Usted no ha sido aplastado por el poder de tus enemigos todavía? Tal vez voy a tener que dar un paso pulg No ¿Sería eso bueno?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_4', 'Mis asesores estaban discutiendo su pequeña guerra. Tengo derecho a decir que creo que es bastante patético.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_5', 'Mi animadores imperio crear muchos trabajos sobre su pequeña guerra. Quiero ser claro, su intento de supervivencia es una especie de broma.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HUMAN_AT_WAR_2', 'Siento mucho lo que está en distraído por la guerra. ¿Te puedo ayudar?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HUMAN_AT_WAR_3', 'Enviar mis saludos a sus hombres en combate. Ahora bien, ¿en qué puedo servirle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_MILITARY_2', 'No creas que no se dan cuenta de su usurpación militar en mis fronteras. Si quieres una pelea, entonces ven. No esconda como un cobarde poco.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_MILITARY_3', 'No estoy amenazado por sus tropas. Ataque mí como el "gran" líder es usted, o sal de mi vista.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_MILITARY_2', 'Mis generales han notado un gran número de tropas cerca de mis fronteras. Respetuosamente le pido a usted a pasar de mi tierra, guardando mis ciudadanos felices. Ahora bien, ¿tienes algo que preguntar de mí?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_MILITARY_3', 'Quiero permanecer en buenos términos con usted, pero el posicionamiento de su ejército está haciendo que sea extremadamente difícil. Por favor, reconsiderar la ubicación de sus tropas o puede que tengamos que abandonar la diplomacia con usted.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_EXPANSION_2', 'La expansión de su gente es una molestia para mí y todo el mundo que nos rodea. Pero no debería tener que preocuparse, sus edificios probablemente erosionará desde esa porquería se llama a una nación.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_EXPANSION_3', 'Me recuerdas a un enjambre de langostas. Usted ira todo el mundo y siguen propagándose hasta que hayas dejado tu devastación de civilidad en el mundo entero. Sólo puedo esperar que te das cuenta de lo grande de un dolor de cabeza se ha convertido en su expansión.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_EXPANSION_2', 'Parece que su expansión ha sido observado por uno de mis asesores extranjeros. Se sospecha que puede quedarse sin espacio y puede abrir las armas contra mi pueblo. Les insto a frenar su expansión. ¿Qué dice usted?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_PLOT_BUYING_2', 'Tirar su dinero en la tierra a su alrededor sólo me da más territorio a la captura. Espero que entiendas esto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_PLOT_BUYING_2', 'Agradezco una compra justa de vez en cuando, pero la velocidad a la que usted está comprando la tierra que nos rodea es alarmante. Les insto a que reconsidere antes tensión entre nuestras naciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_MILITARY_2', 'Su ejército es muy poderoso. Sólo puedo esperar que utilizará su fuerza para el bien y la barbarie no.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_MILITARY_3', 'He oído leyendas de grandes héroes y batallas de tu imperio. Sus enemigos deben temer el día en que dos caminos se cruzan.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_ECONOMY_2', 'Mis comerciantes han quedado satisfechos con las visitas a su gran imperio. Tal vez le gustaría enseñarles algunas cosas acerca de hacer dinero?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_ECONOMY_3', 'Sus arcas son grandes, mientras que la mía permanecen vacíos. Si se pudiera enseñar a mis recaudadores de impuestos como para mantener una economía grande, yo estaría muy agradecido.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_BY_AI_3', 'Me habló en contra de usted por una razón. ¿Por qué me molestes con tus tonterías? Deja.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_AI_3', 'No tan enojado ahora, ¿verdad? Yo no me lo creo. Usted es falso. Fuera de mi vista.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TOO_SOON_FOR_DOF_3', 'Una oferta interesante. Aunque aprecio la cortesía, no he sabido que lo suficiente como para confiar en ti todavía. Tal vez en otro momento?.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TOO_SOON_FOR_DOF_4', 'Pareces un líder amable, pero no me puedo conectar a Alianzas todavía. Tal vez en el futuro?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DOF_NOT_HONORED_3', 'Me bañaba en la mentira y me apuñaló por la espalda! Usted es digno de la atención de nadie. Fuera de mi vista [NEWLINE] [NEWLINE]! ([COLOR_WARNING_TEXT] Han denunciado públicamente nos NOTA:. Usted no está en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DOF_NOT_HONORED_4', 'Me recuerdas a un niño codicioso. Usted quiere demasiado a demasiada gente y se termina con nada. Usted es un mentiroso y ahora todo el mundo va a saber de sus formas backstabbing [NEWLINE] [NEWLINE]. ([COLOR_WARNING_TEXT] Han denunciado públicamente nos NOTA:. Usted no está en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DENOUNCING_FRIEND_3', 'Ha mostrado sus verdaderos colores. La única acción que puede tomar ahora es advertir a otros de su inutilidad [NEWLINE] [NEWLINE] ([COLOR_WARNING_TEXT] Han denunciado públicamente nos NOTA:. Usted no está en guerra [ENDCOLOR]).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DENOUNCING_FRIEND_4', 'Usted no es el tiempo de nadie Worth. Trabajar con usted fue el mayor error de mi vida. No estoy destinado a repetirla [NEWLINE] [NEWLINE]. ([COLOR_WARNING_TEXT] Han denunciado públicamente nos NOTA:. Usted no está en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_2', 'Usted y su pequeño amigo} {1_CivName me enferman. No puedo esperar a ver el día que sus imperios arden en llamas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_2', 'No voy a estar de pie y jugar un tonto, gusano. Si quieres pelear, y luego luchar.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_3', 'Te reto a los ataques. Sus ciudades se verá grandioso decorado con mis banderas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_4', '¿Desea derramar sangre? Luego vienen. No puedo esperar.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_EXPANSION_SERIOUS_WARNING_2', '¿No te lo advierto acerca de su expansión imprudente? Este es un asunto serio. ¡Aléjate de mi tierra, o la acción militar más puede seguir. ¿Entiendes?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DENOUNCE_4', 'Usted es un demonio que no merece el reconocimiento [NEWLINE] [NEWLINE] ([COLOR_WARNING_TEXT] Han denunciado públicamente nos NOTA: Usted no está en guerra [ENDCOLOR].).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DENOUNCE_5', 'Usted es un gasto tonto de tiempo. Rot en su rincón del mundo, como yo no tengo tiempo para ti [NEWLINE] [NEWLINE]. ([COLOR_WARNING_TEXT] Han denunciado públicamente nos NOTA:. Usted no está en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DENOUNCE_6', 'Ahora el mundo sabe que un idiota lleva su nación patético. Usted va a conducir a su gente a una extinción temprana [NEWLINE] [NEWLINE]. ([COLOR_WARNING_TEXT] Han denunciado públicamente nos NOTA:. Usted no está en guerra [ENDCOLOR])', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DOW_LAND_4', 'Nuestras fronteras cercanos han provocado mucha tensión. Ahora es el momento de ver quién está la victoria en el combate.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DOW_LAND_5', 'Es hora de expandir mi imperio. Yo declaro la guerra a usted sin remordimientos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_5', 'Todavía no puedo creer que pensaras que yo era tu aliado. Tu ignorancia será su perdición.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_6', 'La amistad fue bonito mientras duró, ¿no? Siento decírtelo, pero no eran más que un peón en mi gran estrategia. Es hora de morir.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_7', 'Tiempo de morir, cachorro. Jugando el papel de su amigo estaba muy aburrido. Es hora de hacer valer mi dominio.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DOW_WEAK_BETRAYAL_2', 'Sé que tengo una pequeña posibilidad de victoria, pero si no atacamos ahora, sólo se enfrentará a un oponente más fuerte en el futuro.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HUMAN_PLOT_BUYING_WARNING_GOOD_2', 'Estoy feliz de saber que entiende nuestra situación. Espero que podamos continuar con nuestra comprensión mutua.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HUMAN_EXPANSION_WARNING_GOOD_2', 'Gracias por su comprensión. No debemos olvidar esto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_5', 'Estaba tan seguro de que lo entendería. Ha decepcionado a mi pueblo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_6', 'Después de mi vista. Eres una desgracia para mi pueblo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_GOOD_2', 'Muy bien. Me quedo con un ojo hacia fuera para sus ejércitos, sin embargo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_GOOD_3', 'Luego de eso.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_2', 'Patético cachorro. Bueno, supongo que no puedo quejarme de ti. Está en su naturaleza bárbara de tomar decisiones imprudentes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_3', 'Honesto o no, sus decisiones insensatas me ha dejado sin ningún respeto por ti. Ahora, nuestros ejércitos en conflicto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_4', 'Puede que no sea un cobarde, pero eso no quiere decir que no eres un tonto. Usted es una vergüenza para todos los líderes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_AGGRESSIVE_MILITARY_WARNING_BAD_2', 'Usted ha traicionado nuestra confianza. Estoy decepcionado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_AGGRESSIVE_MILITARY_WARNING_BAD_3', 'Si esta es la manera que debe ser, voy a destruirte a ti ya tus ejércitos malvados.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_4', 'Ustedes han demostrado compasión donde otros han demostrado la codicia. Para ello, le aplaudo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_5', 'Por la liberación de nuestros ciudadanos, que han demostrado para ser un aliado más fiable que lo que pensaba. Espero que estas buenas relaciones de poder continuar.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_6', 'Tengo la suerte de recibir mi pueblo otra vez. Usted tiene mi agradecimiento.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_7', 'Has vuelto a estos prisioneros de guerra para mí! Este gesto no debe ser olvidado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_8', 'Gracias por la liberación de mi pueblo. Usted es un líder bondadoso y compasivo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_4', 'Debemos abrir una botella de champán para celebrar su bondad!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_5', 'Estoy muy agradecido por la pantalla desinteresado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_6', 'Nuestra gente sólo puede beneficiarse de esto. Gracias.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_4', '¿Qué trato terminará nuestro sufrimiento mutuo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_5', 'Estoy dispuesto a escuchar las conversaciones de paz. ¿Cuál es su oferta?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_6', 'En nombre de mi pueblo, le insto a llegar a un acuerdo apresurado. ¿Cuál es el precio de la paz?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DOW_DESPERATE_9', 'Mis asesores me puede decir lo contrario, pero yo soy el que manda. Tengo pocas esperanzas de ganar, pero tengo que vencer!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_4', ' Ahora es mi tiempo. Yo me levantaré para convertirse en rey de reyes. Naciones más pequeñas como usted se doblará delante de mi poder!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_7', ' No puedo pasar por alto esta oportunidad para fortalecer mi pueblo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_3', 'Tengo curiosidad por ver a su oferta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_4', 'Por favor, haga su oferta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_5', 'Vamos a ver su propuesta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_5', 'Mientras resolver sin inhibiciones? No. Tengo un derecho a establecerse donde me plazca.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_6', '¿Por qué no? Si no te gusta - hacer algo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_7', 'Por supuesto que no, maldito grosero! Voy a mantener esto en mente. No quiero llevar a cabo negociaciones con un bruto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_RESEARCH_AGREEMENT_OFFER_2', 'El futuro es brillante por delante y mis científicos están dispuestos a explorar lo desconocido! ¿Nos ayudas con un acuerdo de investigación mutua?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_RESEARCH_AGREEMENT_OFFER_3', 'Un grupo de científicos es buena. Pero imagínese lo que ambos podrían lograr? ¿Va a ayudarnos con un acuerdo de investigación?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_LUXURY_TRADE_2', 'Intercambio de estos recursos Lujo haría tanto de nuestro pueblo feliz. ¿Qué dice usted?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_REQUEST_ITEM_4', 'Malos tiempos han caído sobre mi pueblo. Solicito su ayuda para ayudarnos. ¿Puede usted ahorrar esto para un amigo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_REQUEST_ITEM_5', 'Amigos que ayudarnos el uno al otro. He caído en tiempos difíciles. ¿Nos puedes ayudar? Yo no lo olvidaré.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_PEACE_OFFER_4', 'Creo que ha llegado el momento de deponer las armas y negociar la paz. ¿Va a firmar un tratado de paz con nosotros?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_PEACE_OFFER_5', 'Nosotros no somos bárbaros sedientos de sangre. Debemos poner fin a esta guerra mientras aún tenemos nuestra dignidad. ¿Va a aceptar este trato?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_PEACE_OFFER_6', 'Hoy en día un niño se me acercó y me dijo que su padre fue matado por sus hombres. Su vida estaba arruinada. Estas personas son nuestro futuro. No debemos traicionar. Por favor, acepta esta ofrenda de paz.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_WORK_WITH_US_YES_4', 'Excelente. Espero una Alianza que va a cambiar el mundo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_WORK_WITH_US_YES_5', 'Yo amablemente aceptar, en los términos que beneficien a ambas naciones y luchar contra el mal que se encuentran fuera de nuestras fronteras.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_INSULT_GENERIC_3', 'No sé cómo su imperio se las arregla para mantenerse a flote. Uno pensaría que su gente se uniría a un imperio digno de vivir pulg Ahora que lo pienso, mi nación sería un gran destino!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_WARMONGER_5', 'La gente está empezando a hablar de ti. Parecen pensar que favorecen la discusión sobre genocidio humano civil.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_WARMONGER_6', 'Usted sembrar el miedo en el que caminar. Las otras naciones están empezando a preocuparse. No puedo decir lo mismo de mí mismo, yo sólo creo que eres un idiota balbuceante.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_BOOT_KISSING_3', 'Oh, gran líder. Te ruego que me puede enseñar la forma de dirigir un imperio tan grande como el tuyo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_3', 'Por favor, te lo ruego. Mostrar misericordia de mi pueblo, que no desea morir.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_4', 'Has roto mi pueblo a la nada. Su voluntad se rompe, destrucción de sus hogares, las familias se dispersaron. Sólo puedo esperar que usted ha venido a poner fin a esta locura antes de que más inocentes mueran.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLOMACY_FRIENDSHIP_ADV_QUEST', 'Alianza', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLOMACY_FRIENDSHIP_HEADING3_BODY', 'Una alianza entre dos o más jugadores significa, en el sentido más básico que estos jugadores tienen un interés mutuo en busca el uno al otro (por lo general se trata de convertirse en socios comerciales y el intercambio de recursos). Los jugadores también pueden optar por establecer bloques de aliados con varios jugadores con el fin de garantizar la seguridad a largo plazo y la estabilidad económica.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLOMACY_FRIENDSHIP_HEADING3_TITLE', 'Alianza', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_NTFN_DOF_S', '{1_PlayerName} aliado con {2_PlayerName}!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_NTFN_DOF', '{1_CivName: textkey} y {2_CivName: textkey} formado una alianza, forjando un fuerte vínculo entre los dos imperios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_NOTIFICATION_DOF_S', '{1_PlayerName} aliado con {2_PlayerName}!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_NOTIFICATION_DOF', '{1_CivName: textkey} y {2_CivName: textkey} formado una alianza, forjando un fuerte vínculo entre los dos imperios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_NOTIFICATION_FRIENDSHIP_EXPIRED_S', 'Alianza caducado', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_NOTIFICATION_FRIENDSHIP_EXPIRED', 'Su alianza con {1_LeaderName}  siguió su curso y tendrá que ser renovado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_DOF', '[COLOR_POSITIVE_TEXT] Somos aliados! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_MUTUAL_DOF', '[COLOR_POSITIVE_TEXT] Estamos aliados con los mismos líderes! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_HUMAN_DOF_WITH_ENEMY', '[COLOR_NEGATIVE_TEXT] Se alió con uno de sus enemigos! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_REFUSED_REQUESTS', '[COLOR_NEGATIVE_TEXT] Se negó la solicitud formulada por este jugador después de formar una Alianza! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_HUMAN_FRIEND_DECLARED_WAR', '[COLOR_NEGATIVE_TEXT] Hemos declarado la guerra a ellos, mientras que los aliados! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_HUMAN_FRIEND_DENOUNCED', '[COLOR_NEGATIVE_TEXT] Hemos denunciado mientras aliado! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_HUMAN_DECLARED_WAR_ON_FRIENDS', '[COLOR_NEGATIVE_TEXT] Usted declaró la guerra a los aliados! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_HUMAN_DENOUNCED_FRIENDS', '[COLOR_NEGATIVE_TEXT] Usted denunció sus aliados! [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_DISCUSS_MESSAGE_DEC_FRIENDSHIP', 'Vamos a formar una alianza?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DEC_FRIENDSHIP_1', 'Es difícil encontrar amigos dignos en este mundo. Me gustaría formar una alianza, informando a los otros imperios de la unión entre nuestros pueblos. ¿Qué me dices?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DEC_FRIENDSHIP_2', 'Gran líder, ¿le importaría aliarse con nosotros? Mi esperanza es que podamos construir una relación duradera entre los dos imperios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DEC_FRIENDSHIP_3', 'Vamos a plantar las semillas de un futuro brillante por la formación de una Alianza. ¿Qué dice usted?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DEC_FRIENDSHIP_4', 'Este mundo puede ser cruel. Sólo con la ayuda de otras personas podemos superarlo. Vamos a anunciar al mundo que somos aliados?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DEC_FRIENDSHIP_5', 'Hola, gran líder. ¿Va a aliarse con nosotros para fortalecer el vínculo de nuestros imperios?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DEC_FRIENDSHIP_6', 'Sólo mediante la formación de una alianza pueden nuestros imperios realmente ser una fuerza temible en nuestro mundo. ¿Va a unirse a mí en este honor?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DEC_FRIENDSHIP_7', 'Estoy buscando a un amigo en este mundo. ¿Va a aliarse con nosotros?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DEC_FRIENDSHIP_8', 'Gran líder, le aliarse con nosotros? Esto haría que nuestros amigos cantan de alegría y los enemigos se acobardan ante nuestras fuerzas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_3', 'Parece que me han lanzado un ataque contra uno de sus estados de ciudad. No debería ser una pérdida para usted, sino que probablemente estaban planeando puñaladas por la espalda de todos modos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_4', 'En este momento estoy celebrando con una gran fiesta. Lanzar un ataque en contra de su pequeña ciudad estado fue la mejor decisión que he hecho en años.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_5', 'Oh, ¿me oyes algo? Creo que puede ser el llanto de su protectorado poco. No te preocupes, mis hombres se les silenciar pronto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_3', 'Mi más sentido remordimientos, pero parece que en un protectorado de los suyos que ha tenido relaciones en el pasado ha sido atacado por mis ejércitos. Tenga la seguridad de que esta decisión no se hizo en su contra, pero se consideró necesario un curso de acción.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_4', 'Perdóname, pero mis ejércitos pueden haber sobrepasado sus límites y atacó a una ciudad-estado que ha tratado en el pasado. ¿Por favor ofrezca su perdón?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_5', 'Aunque mi intención no era agriar las relaciones con ustedes, mis ejércitos han atacado una ciudad-estado en el que se han ocupado de que en el pasado. Me gustaría repetir que esto no era un curso de acción hostil hacia usted.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_DISCUSS_SETTLE_WHAT_WE_PLEASE', 'Vamos a expandir donde nos plazca.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_DISCUSS_SORRY_FOR_SETTLING', 'Lo sentimos, vamos a abstenerse de resolver cerca de usted.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_EXPANSION_WARNING_5', 'Me he dado cuenta de sus fronteras se han ido expandiendo a un ritmo alarmante. Asentamiento ciudades cerca de mi territorio podría arruinar cualquier posibilidad de la amistad de nuestros pueblos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_EXPANSION_WARNING_6', 'Historias antiguas de mi pueblo hablan de una migración desde las tierras que actualmente ocupan. Nosotros le permitirá mantener las tierras por ahora, pero no dudaremos en tomar medidas si se expande más allá en las tierras de nuestros antepasados.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_VICTORY_DISPUTE', '[COLOR_NEGATIVE_TEXT] Si piensan que su conflicto de intereses con el de ellos, y no me gusta!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_MINOR_THIS_GUY_WANTS_WAR', 'El líder que está hablando no tiene ningún deseo de ir a la paz en esta nación.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DIPLO_MINOR_OTHER_GUY_WANTS_WAR', 'El otro líder no tiene deseos de ir a la paz con el líder que está hablando.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_PROGRESS_SCREEN_GOLD', 'Las naciones más ricas del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_PROGRESS_SCREEN_HAPPINESS', 'Los países más felices del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_PROGRESS_SCREEN_POWER', 'The Strongest Militares del Mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_PROGRESS_SCREEN_PRODUCTION', 'Las naciones más productivas del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_PROGRESS_SCREEN_SCIENCE', 'Las naciones más avanzadas del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_PROGRESS_SCREEN_WONDERS', 'Las naciones más artísticas del mundo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DUDE_8', 'Platón', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DUDE_9', 'Aristóteles', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DUDE_10', 'Magallanes', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DUDE_11', 'Charles Dezobry', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DUDE_12', 'Louis Halphen', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_DUDE_13', 'Barclay', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_5', 'Es el exterminio total de lo que realmente quieres? Le ruego que nos sobra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_6', 'Esta guerra nos ha destruido. No te puedo ofrecer nada más que la paz ahora. Por favor, muestre misericordia.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_7', 'Mi pueblo no tiene ninguna posibilidad de sobrevivir. Nuestro destino está en tus manos, ahora. Por favor, perdónanos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_8', 'Ha reducido mi imperio a nada más que un montón de escombros glorificado. Le ruego que nos permiten reconstruir. Por el bien de nuestras relaciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ALEXANDER_FIRSTGREETING_4', 'Líder Saludos, ¡excelente! Usted es el líder poderoso que Hermes dijo que entraría en mi presencia?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ALEXANDER_FIRSTGREETING_5', 'Que los dioses estar con los dos, gran líder. Yo soy Alejandro III, rey de Grecia todopoderoso! Me siento honrado de conocerte.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ALEXANDER_FIRSTGREETING_6', 'Saludos, un extraño! Yo soy Alexander, príncipe de los poderosos griegos! Rezo para que usted cenar con los olímpicos, y desprecian a los Titanes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ASKIA_FIRSTGREETING_5', 'Por la gloria de Dios, te doy la bienvenida. Soy Askia, gobernante de la Songhai poderoso. Que nuestras dos naciones convivan en paz.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ASKIA_FIRSTGREETING_6', 'Líder Saludos, genial. Espero que estén tan sabio un hombre como yo. Que la gracia de Dios mantener nuestras dos naciones de un conflicto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ASKIA_FIRSTGREETING_7', 'Saludos! Soy Askia de la valiente Songhai. Yo deseo que tú eres un buen amigo y no un enemigo lamentable.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_AUGUSTUS_FIRSTGREETING_4', 'Por favor, ven más cerca. Mi nombre es César Augusto, emperador del imperio glorioso de Roma. Ruego usted es un líder digno de confianza.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_AUGUSTUS_FIRSTGREETING_5', 'El pueblo romano darle la bienvenida. Yo soy Augusto, emperador de esta poderosa nación. Algún día, mi nación crecerá para convertirse en el más grande que el mundo haya visto!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_AUGUSTUS_FIRSTGREETING_6', 'Bienvenido a Roma, gran líder. Voy a servir a nuestro mejor vino para celebrar la reunión de nuestras dos naciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_BISMARCK_FIRSTGREETING_4', 'Bienvenido, gran líder. La poderosa nación de Alemania le da la bienvenida. Mi nombre es Bismarck. Ruego su trato con el pueblo alemán son sólo positivos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_BISMARCK_FIRSTGREETING_5', 'Alemania le da la bienvenida a nuestra tierra, forastero. I, Otto van Bismarck, esperamos que nuestras naciones se quedarán en buenos términos y beneficiarse de esta reunión ..', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_BISMARCK_FIRSTGREETING_6', 'Yo te saludo, Stranger. Bienvenidos a Alemania. Mi nombre es Bismarck. ¿Te gusta el sabor de este vino exquisito?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_CATHERINE_FIRSTGREETING_4', 'Dios te salve, Stranger. Soy Catalina, emperatriz de Rusia. Espero que podamos llevarnos bien también.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_CATHERINE_FIRSTGREETING_5', 'Salve, gran líder. Bienvenido a la tierra gloriosa de Rusia. Nuestra gente es valiente, y los hombres aquí son increíbles! Si los temas son como la mía, entonces seremos la envidia de todo el mundo!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_CATHERINE_FIRSTGREETING_6', 'Excelente para cumplir con usted, gran líder. ¿Cómo puede el pueblo ruso hermosas servirle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_DARIUS_FIRSTGREETING_4', 'Dios te salve, líder. Yo soy Darío de Persia poderosa. Nuestro pueblo será la envidia de todo el mundo algún día.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_DARIUS_FIRSTGREETING_5', 'Saludos, líder. Mi nombre es Darío, poderoso of Persia! Sólo puedo esperar que va a llegar a ser un aliado beneficioso.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_DARIUS_FIRSTGREETING_6', 'Bienvenido a Persia, Stranger. Yo soy Darío. Manténgase dentro de sus límites y de nuestra relación debe estar bien.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ELIZABETH_FIRSTGREETING_5', 'Inglaterra le saluda, líder. Yo soy la reina Isabel. ¿En qué podemos ayudarle?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ELIZABETH_FIRSTGREETING_6', 'Bien hallado. Espero que sea de utilidad para Inglaterra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ELIZABETH_FIRSTGREETING_7', 'Excelente para ver otra cara de este mundo. Soy Elizabeth, la reina de la poderosa Inglaterra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_GANDHI_FIRSTGREETING_4', 'Saludos compañeros, igual! Cantemos en reverencia a nuestro conocimiento newfound!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_GANDHI_FIRSTGREETING_5', 'Dios te salve, líder. ¿Has venido a difundir la palabra de la paz en el mundo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_GANDHI_FIRSTGREETING_6', 'Saludos. Yo soy Gandhi, de la India pacífica. Ruego a nuestros dos naciones se reunirán y aún más nuestras naciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_HARUN_FIRSTGREETING_4', 'Por la gracia de todo lo que es glorioso, I, Harun-al-Rashid, le dan la bienvenida a la tierra árabe.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_HARUN_FIRSTGREETING_5', 'Por favor, tomen asiento líder, genial. Soy Harun-al-Rashid, y la historia siempre recordará esta reunión.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_HARUN_FIRSTGREETING_6', 'Bienvenido a Arabia, gran líder. Mi nombre es Harun al-Rashid. ¿En qué puedo ayudarle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_HIAWATHA_FIRSTGREETING_4', 'Los espíritus han anunciado su llegada, gran líder.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_HIAWATHA_FIRSTGREETING_5', 'Saludos. Soy Hiawatha, líder del poderoso iroqueses. Espero que compartan nuestro amor por la naturaleza.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_HIAWATHA_FIRSTGREETING_6', 'Dios te salve, Stranger! Los espíritus han dicho muchas cosas sobre tu pueblo, y me alegro de que finalmente te conocí.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_5', '¡Ah! ¿Quién es usted y por qué usted se atreve a presentarse ante el poderoso Montezuma?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_6', 'Soy Montezuma, líder de los aztecas especiales! ¿Y usted? Usted es sólo uno de mis muchos temas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_7', 'Cuidado, Stranger! Siempre hay sacrificios que deben hacerse en el imperio de Moctezuma!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_8', '¿Desea aliarse con Moctezuma poderoso, forastero? Manden sus débiles y fuertes, sus mujeres y sus hijos! Vamos a derramar sangre en nombre de nuestros dioses!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_NAPOLEON_FIRSTGREETING_5', 'Francia le da la bienvenida, Stranger. Yo soy Napoleón, temido general en todo el mundo! Ora usted sigue siendo aliado de Francia.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_NAPOLEON_FIRSTGREETING_6', 'Saludos, forastero. Yo soy Napoleón de Francia. Los franceses no fallar a nadie, tenga esto en cuenta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_NAPOLEON_FIRSTGREETING_7', 'Saludos. Yo soy Napoleón, y mi pueblo somos audaz y valiente. Sólo se puede esperar son los suyos, también.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_NAPOLEON_TRIBUTE_NO_ANGRY', '¿Cómo te atreves a exigir tributo de mi!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_NAPOLEON_DECLAREWAR_4', 'Oh, estoy seguro que has oído! Mis ejércitos franceses corriendo de entre los árboles y desde lo alto de las colinas para destruirlos de una vez por todas!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ODA_FIRSTGREETING_4', 'Dios te salve, líder. Espero que estén tan honorable y valiente que yo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ODA_FIRSTGREETING_5', 'Bienvenido a Japón. Soy Oda Nobunaga. Rezo para que usted seguirá siendo un fiel compañero, o un adversario digno.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_ODA_FIRSTGREETING_6', 'Soy Oda Nobunaga, y les doy la bienvenida al gran imperio de Japón. Que vuestra estancia aquí sea tranquilo y agradable.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_RAMESSES_FIRSTGREETING_4', 'Saludos, mortal. Yo soy Ramsés, el faraón de la gloriosa Egipto. ¿Has venido a inclinarse ante mi trascendencia?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_RAMESSES_FIRSTGREETING_5', 'Yo soy Ramsés, y ha entrado en la presencia de mi divinidad infinita.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_RAMKHAMHAENG_FIRSTGREETING_4', 'Bienvenido a Siam, forastero! Estoy Ramkhamhaeng, rey de este pueblo glorioso!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_RAMKHAMHAENG_FIRSTGREETING_5', 'Salve, gran líder! Bienvenido a Siam. Le invitamos a visitar mi jardín cuando le apetezca.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_RAMKHAMHAENG_FIRSTGREETING_6', 'Saludos! Bienvenido a Siam. Tenga en cuenta, los malos no son bienvenidos en mi tierra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_SULEIMAN_FIRSTGREETING_4', 'Yo te saludo, Stranger! Espero llegar a ser un líder digno!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_SULEIMAN_FIRSTGREETING_5', 'Salve, gran líder! Os saludo y darle la bienvenida a mi corte! Soy Suleiman, jefe de los otomanos!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_SULEIMAN_FIRSTGREETING_6', 'Bienvenido al Imperio Otomano, líder! Tengo el placer de conocerte!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_WASHINGTON_FIRSTGREETING_4', 'América le da la bienvenida, líder. Espero que se encuentre una regla de la libertad y la prosperidad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_WASHINGTON_FIRSTGREETING_5', 'Bienvenido a los Estados Unidos, extranjero. Estoy Washington, gran gobernante del mundo libre. ¿Cómo podemos ayudar?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_WASHINGTON_FIRSTGREETING_6', 'Hola, jefe. Bienvenido a América, la tierra de los libres y el hogar de los valientes. Ahora, dime, ¿qué valor tiene para nosotros?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_WUZETIAN_FIRSTGREETING_4', 'Saludos! Soy Wu Zetian, de China. ¿Cómo puede mi pueblo ayudarle hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_WUZETIAN_FIRSTGREETING_5', 'Esta reunión será celebrada por nuestros descendientes por venir! Bienvenido a China, Stranger!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_LEADER_WUZETIAN_FIRSTGREETING_6', 'Saludos! ¿Cómo puedo, Wu Zetian de la poderosa China, ayudar a usted y su gente extranjera?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_LUXURY_TRADE_3', 'Nuestro pueblo exige un estilo de vida lujoso. No estás de acuerdo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_LUXURY_TRADE_4', 'De acuerdo a este acuerdo y que hará que los sujetos de ambos nuestras tierras mucho más feliz.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_LUXURY_TRADE_5', 'Es hora de que comercializan nuestros lujos para proporcionar una vida mejor para nuestro pueblo. No estás de acuerdo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_3', 'Primero tuve que lidiar con {1_CivName}, ahora tengo que tratar con usted? Patético. Los dos sois la razón por la que este mundo es un hervidero condenados de escoria y villanía.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_4', 'Qué bonito. Le parece y {1_CivName} son aliados. Está bien, porque voy a aplastar a ambos bajo la fuerza de mi imperio.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_EXPANSION_3', 'Lo siento, pero el ritmo de expansión entre las personas es preocupante para mí. Comprendo sus deseos de ampliar su influencia, pero nuestro pueblo está alarmado conseguir y se sienten amenazados. Sólo le pido si usted desea reducir la velocidad y dejar un poco de espacio para todos, entonces todos podemos llevarnos mejor. ¿Estás de acuerdo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_PLOT_BUYING_3', 'Mantenga gastar su dinero en la compra de tierras sin valor. Es probable que ni siquiera puede mantenerlos a salvo de los animales salvajes, no menos que los ejércitos de los que te odian.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_PLOT_BUYING_3', 'Parece que has estado comprando tierras en torno a nuestro territorio demasiado rápido. Mis pacientes no se dan cuenta de esto. Les insto a que cese sus acciones, por el bien de nuestros dos pueblos y para aliviar nuestras tensiones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_14', '¿Por qué has venido? Para rogar por la paz? ¡No! Mis ejércitos conducirá a sus ciudades a tierra, las mujeres y los niños primero!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_15', 'Nunca me detendré hasta que malos son destruidos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_16', '¡Fuera! Esta guerra continuará hasta que estoy listo para darle la misericordia!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_15', 'Salve, amigo! ¿Qué es lo que necesita de mi imperio?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_16', 'Encantado de verte de nuevo. ¿Has venido a solicitar un comercio?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_17', 'Es genial ver a un amigo, otra vez. Lo que importa vamos a discutir hoy?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_6', 'Estoy escuchando.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_7', '¿Y bien? Vamos a escuchar su propuesta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_3', 'Lo siento, yo no creo que este acuerdo puede trabajar.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_4', 'No parece ser una forma lógica de hacer este trato.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_5', 'Lo siento, no puedo ver a un trato justo en cualquier parte de esta oferta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_6', 'No, lo siento. Este acuerdo no puede funcionar.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_4', 'Eso es lo mejor que puede ofrecer? ¿Por quién me tomas, por un tonto?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_5', 'Vamos, tendrás que hacerlo mejor si usted desea conseguir un acuerdo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_6', 'No podía avergonzar a mi pueblo con semejante trato ultrajante. Vas a tener que hacerlo mejor que eso.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_4', 'No puedo aceptar este acuerdo. Por favor, ponga más sobre la mesa.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_5', 'Este acuerdo no es bueno para mis intereses. Ofrecer más y podemos tener un trato justo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_6', 'Creo que tiene que ofrecer un poco más por este acuerdo para trabajar. Pero tal y como está ahora, esto no es aceptable.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_HUMAN_CONCESSIONS_3', 'Este acuerdo será recorrer un largo camino para hacernos amigos de nuevo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_HUMAN_CONCESSIONS_4', 'No nos olvidaremos de este acuerdo, te prometo esto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_4', 'Esta operación se lleva a cabo, entonces. Que nuestro comercio continuará en el futuro.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_5', 'A continuación, los términos se establecen. Este fue un trato justo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_6', 'Estoy de acuerdo con este acuerdo. Es justo para ambas partes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_4', 'No puedo negar esta oferta generosa de los suyos. Gracias.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_5', 'Se han sacrificado sus bienes para hacer que nuestra relación mejor. Para ello, le aplaudo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_6', 'Su generosidad no ha pasado desapercibida. Gracias.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_7', 'Recordaremos a este acuerdo en los próximos años. Gracias por ser tan generoso.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_3', 'Vamos a abrir nuestras fronteras para dar paso a futuro comercio y la comodidad?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_4', 'Creo que es lo mejor para nosotros, como naciones civilizadas del mundo, para abrir nuestras fronteras y dejar que nuestra amistad prosperar. ¿Qué dice usted?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_5', 'Vamos a abrir nuestras fronteras a los demás. Eso sólo servirá para aumentar la amistad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DEMAND_2', 'Vamos a ser real. Tú tienes algo que yo quiero, y no se la entrega, usted puede esperar para ver a mis tropas a llamar a su puerta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GENERIC_DEMAND_3', 'Dame lo que quiero, y tus mujeres y los niños se mantengan seguros. Eso suena como un buen negocio, ¿no te parece?', '', '');
